    ) external pure returns (bool) {
        return settlementToken.gte(amountX10_18, decimals);
    }

    function testGte(
        int256 settlementToken,
        // solhint-disable-next-line var-name-mixedcase
        int256 amountX10_18,
        uint8 decimals
    ) external pure returns (bool) {
